(window.webpackJsonp=window.webpackJsonp||[]).push([[25],{331:function(_,v,t){_.exports=t.p+"assets/img/snipaste20220523_094218-8116418.34ea4e99.jpg"},332:function(_,v,t){_.exports=t.p+"assets/img/snipaste20220523_094350-8116418.4d0979e0.jpg"},504:function(_,v,t){"use strict";t.r(v);var s=t(4),a=Object(s.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("h2",{attrs:{id:"致法学新人的一封信-与得相随"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#致法学新人的一封信-与得相随"}},[_._v("#")]),_._v(" 致法学新人的一封信 - 与得相随")]),_._v(" "),v("p",[_._v("纳尼亚传奇的作者CS路易斯说过，如果把人类想象成一队列队前行的舰队，那么，船只之间不能互相碰撞，阻碍彼此的航道；另外，每艘船必须状态良好，能经风浪。前者就是人与人之间的关系，而后者就是自身的德行。")]),_._v(" "),v("img",{attrs:{src:t(331)}}),_._v(" "),v("p",[_._v("当前的中国，每个人都对现状不满意，无论是穷人还是富豪，平民还是官员，都牢骚满肚。不可否认，中国现在面临很多问题，贪污腐败、道德沦丧、罪恶横行，但这些问题出在哪呢？有的人说在制度，有的人说在国民性，但很少有人说：在我！")]),_._v(" "),v("p",[_._v("是啊，如果船只破败不堪，根本无法行驶，叫他们如何行驶，以免相互碰撞，又有什么意义。对于中国的问题，我们每个人都难辞其咎，正是我们的贪婪、自私、放纵、堕落、胆怯导致了这种种的问题。")]),_._v(" "),v("p",[_._v("我以前对这个社会也充满抱怨，但我现在发现抱怨于事无济，如果你想改变，就从改变自身开始，做一个有德行的人，正如蝴蝶翅膀的震动，能够改变整个亚马逊地区的气候，每个人微小的善意必能影响这个社会。")]),_._v(" "),v("p",[_._v("在英国威斯特敏斯特大教堂有一块著名的墓碑，墓碑主人不详。这个教堂是英国最著名大教堂之一，许多名人埋葬于此，如丘吉尔、莎士比亚、牛顿、狄更斯等等，还有英国二十多位前国王的墓碑。与这些人比起来，这块墓碑非常普通，但它却是名扬全球的一块著名墓碑，每一个看过这块碑文的人都深深地为止感动。")]),_._v(" "),v("p",[v("img",{attrs:{src:t(332),alt:""}})]),_._v(" "),v("p",[_._v("墓碑上，刻着这样的话：")]),_._v(" "),v("p",[_._v("当我年轻的时候，我的想象力从没有受过限制，我梦想改变这个世界。")]),_._v(" "),v("p",[_._v("当我成熟以后，我发现我不能够改变这个世界，我将目光缩短了些，决定只改变我的国家。")]),_._v(" "),v("p",[_._v("当我进入暮年以后，我发现我不能够改变我的国家，我的最后愿望仅仅是改变一下我的家庭。但是，这也不可能。")]),_._v(" "),v("p",[_._v("当我现在躺在床上，行将就木时，我突然意识到：如果一开始我仅仅去改变自己，然后作为一个榜样，我可能改变我的家庭；在家人的帮助和鼓励下，我可能为国家做一些事情。")]),_._v(" "),v("p",[_._v("然而，谁知道呢？我甚至可能改变这个世界！")]),_._v(" "),v("p",[_._v("要想改变世界，你必须从改变你自己开始。要想撬起世界，你必须把支点选在自己的心灵上。")]),_._v(" "),v("p",[_._v("我邀请大家，做一个有德行的人，从改变自己开始，改变这个社会。")]),_._v(" "),v("p",[_._v("有四种德行为文明人所认可，他们分别是谨慎、节制、公正、坚毅。")]),_._v(" "),v("h3",{attrs:{id:"_01-谨慎"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_01-谨慎"}},[_._v("#")]),_._v(" 01.谨慎")]),_._v(" "),v("p",[_._v("谨慎就是在行动时要慎重考虑它的后果。冲动是魔鬼，做任何事情的时候不要过分冲动，要控制自己的情感，古语所谓三思而后行。做事慢几拍总比做了之后再后悔要强的多。")]),_._v(" "),v("p",[_._v("谨是从言字旁，这其实就是提醒我们说话要慎重。繁体字的说（説），让我们在说话是要想想这句话能否兑现，能够兑现的言语才能说出来，所以不要轻易许诺，在许诺前要仔细想想，你能否兑现你的言语。繁体中文非常美妙，每个汉字都是一个图画，都在教导我们做人的道理，可惜简体字改革糟蹋了汉字。比如和说搭配的听（聴）。繁体字的听，他要求我们听的时候要用耳朵、眼睛和心来聆听，这样你才能准确的理解他人的意思。而现在很少有愿意聆听别人的人，基本上在别人还没有说完话，就开始插话，于是在听别人说话，还不如说是在听自己说话，简体字的“听”似乎也反应了这种情况，听不再用耳朵，而用的是“口”，而且是根据对方的“斤”两来决定我到底听不听他的。我现在在学写繁体字，越学越能感受到中文的美。")]),_._v(" "),v("p",[_._v("慎是从心字旁，慎重思考才会是你真实意思的表示，所以慎字的组合，是“真心”。")]),_._v(" "),v("p",[_._v("非常有趣的是，无论是谨，还是慎，言字旁有三横，谨慎的谨中有三横，慎的慎中也有三横，这是不是也在提醒我们三思呢？")]),_._v(" "),v("h3",{attrs:{id:"_02-节制"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_02-节制"}},[_._v("#")]),_._v(" 02.节制")]),_._v(" "),v("p",[_._v("现代社会是一个崇尚个体自由的时代。每个人都希望自己的自由，但我们的自由经常让我们不自由，一如卢梭所说“人生而自由，却无往不在枷锁之中”。")]),_._v(" "),v("p",[_._v("大部分人所理解的自由，就是随心所欲的自由，就像柳宗元的那首诗“破额山前碧玉流，骚人遥驻木兰舟，春风无限潇湘意，欲采蘋花不自由”。")]),_._v(" "),v("p",[_._v("但这种随心所欲的自由经常让我们不自由，因为一旦欲望满足，你又会渴望另一种新的欲望，在追求自由的过程中，你其实成为了欲望的奴隶。赚多少钱会让你满意呢？再多一点；多少个点赞会让你舒服呢？再来一个。我越来越觉得，这种自由可能不是真正的自由。")]),_._v(" "),v("p",[_._v("比如抽烟，想抽什么烟就能抽什么烟，这是一种自由，但还有一种自由是有很多烟放在你面前，你可以抽但你愿意不抽。你想开什么车就开什么车，这是一种自由，但还有一种自由，是你有钱购买任何好车，但你不开，你宁愿去挤地铁公车，因为你发现很多人每天都在拥挤的公车地铁中，你愿意和他们同甘共苦。")]),_._v(" "),v("p",[_._v("有一个人叫做甘地，印度的圣雄，他经常坐火车出行，都坐的是三等座，以此观察民众的疾苦，有一次他刚上火车，一只鞋子掉落到铁轨旁，此时火车已经开动，鞋子无法再捡回来。于是甘地急忙把穿在脚上的另一只鞋子也脱下扔到第一只鞋子旁边。一位乘客不解地问他为什么这样做，甘地笑着说：这样一来，看到铁轨旁鞋子的穷人就能得到一双鞋子了。去英国访问时坐轮船的末等舱，连见英国女王也穿着自制的土布衣。大家觉得是甘地自由，还是华衣美食，出行必做头等舱的人自由呢？（推荐大家看一看《甘地传》这部影片。）")]),_._v(" "),v("p",[_._v("因此，自由其实是自我限制的自由，是可以节制的自由。")]),_._v(" "),v("p",[_._v("人有很多欲望，欲望本身没有错，但如果把任何一种欲望推向极限，作为自己人生的目标，那必然把你带向毁灭。我想特别提醒各位，要限制玩网络游戏的欲望，当然我不是说不能玩游戏，但一定要节制。时间是宝贵的，我们唯一可以支配的最宝贵的财富就是我们的时间，因为一定要把时间投入在最重要的事情上。沉溺于网络游戏，你可能很难适应真实的世界。")]),_._v(" "),v("h3",{attrs:{id:"_03-公正"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_03-公正"}},[_._v("#")]),_._v(" 03.公正")]),_._v(" "),v("p",[_._v("大家来到法大，这是一个以法科为主的院校，而法律最重要的价值就是追求公平和正义，也就是公正。当然，公正不仅仅是法庭的审理，它包括很多美好的价值，如诚实、正直、守信等等。")]),_._v(" "),v("h3",{attrs:{id:"_04-坚毅"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#_04-坚毅"}},[_._v("#")]),_._v(" 04.坚毅")]),_._v(" "),v("p",[_._v("坚毅其实就是勇敢，它包括在危险时表现出的勇气和在挫折时不屈不饶的顽强。在泰坦尼克沉没时，那么危险的时候，人们居然主动让妇女和儿童先走，人性的高贵在那时彰显无遗。在轮船倾覆之时，乐队仍然能够从容奏乐，勇于面对死亡，这就是勇气了，顺便说一句，当时乐队的奏响歌曲也正好是2012年伦敦奥运会开幕式的主题曲（Abide with Me）。我并不期待各位有如此的勇气，我只是在想，当你面对被压伤的小月月，你有没有勇气把她送去医院，面对被撞伤的路人，即便有被讹诈的危险，你有没有勇气送他去医院。前段时间上映的大片《敦刻尔克》，让我最感动的就是普通民众的勇气。人生不会是一帆风顺的，每一天其实都像一场战斗，我们每天都需要勇气来笑对人生。")]),_._v(" "),v("p",[_._v("在挫折中的顽强刚才也有提过，我始终认为能够在挫折中勇敢的爬起来这才是真正的强者，前几天，南京有人申请去美国的签证，被拒签，结果从18楼跳下来。每年在法大的办公楼，昌平第一高楼（14层楼），都有人选择了跳楼这种惨烈的结束生命的方式，理由多种多样。我常想，连死亡你都不怕，那你为什么会害怕生呢？")]),_._v(" "),v("p",[_._v("最后，我想以马丁路德金的话结束这封冗长的信件。")]),_._v(" "),v("p",[_._v("他说：我们必须接受失望，因为失望是有限的，但我们永远不能放弃希望，因为希望是无限的。")])])}),[],!1,null,null,null);v.default=a.exports}}]);